.calendar-control-section {
    max-width: 300px;
    margin: 0 auto;
}

/* lable styles */

#date_label {
    margin-top: 20px;
    display: block;
    color: rgba(0, 0, 0, 0.58);
    font-weight: 500;
    margin-left: 5px;
}
body.highcontrast #date_label {
    color: white;
}

.e-calendar .e-content td.e-selected span.e-day.sf-icon-cup.highlight:before,
.e-calendar .e-content td.e-selected span.e-day.sf-icon-bell:before,
.e-calendar .e-content td.e-selected span.e-day.sf-icon-start.highlight:before {
    color: #ffff !important;
}

.sf-icon-cup:before {
    content: '\e724';
    color: #0b0bd9a1;
    position: relative;
    top: 2px;
    left: -2px;
}

.sf-icon-bell:before {
    content: '\e728';
    color: #0b0bd9a1;
    position: relative;
    top: 2px;
    left: -2px;
}

.sf-icon-start:before {
    content: '\e708';
    color: #0b0bd9a1;
    position: relative;
    top: 2px;
    left: -2px;
}

/* custom generated icons styles */
@font-face {
    font-family: 'e-sb-icons';
    src:
        url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

[class^="sf-icon-"],
[class*=" sf-icon-"] {
    font-family: 'e-sb-icons' !important;
    speak: none;
    font-size: 15px;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

/* end of custom generated icons styles */

