import * as React from 'react';
import * as ReactDOM from 'react-dom';
import { SampleBase } from './sample-base';
import { ListViewComponent, Inject, Virtualization, SelectEventArgs } from '@syncfusion/ej2-react-lists';
import { SplitterComponent, PanesDirective, PaneDirective } from '@syncfusion/ej2-react-layouts';
import { Browser } from '@syncfusion/ej2-base';


/**
 *  Sample for list-view in splitter
 */

export class DetailsView extends SampleBase<{}, {}> {

    public splitterInstance: SplitterComponent;
    public listViewObj: ListViewComponent;

    private firstPaneSize = "35%";

    private firstPaneMinSize = "25%";

    private secondPaneSize = "65%";

    private secondPaneMinSize = "45%";

    private commonData: { [key: string]: string | object }[] = [];
    private dataSource: { [key: string]: { [key: string]: string | object }[] } = {};
    private liElement: HTMLElement;

    constructor() {
        super();
        this.commonData = [
            { name: 'Margaret', imgUrl: 'https://ej2.syncfusion.com/demos/src/listview/images/margaret.png', id: '1' },
            { name: 'Laura', imgUrl: 'https://ej2.syncfusion.com/demos/src/listview/images/laura.png', id: '2' },
            { name: 'Robert', icon: 'R', id: '3' },
            { name: 'Albert', imgUrl: 'https://ej2.syncfusion.com/demos/src/listview/images/albert.png', id: '5' },
            { name: 'Michale', icon: 'M', id: '4' }
        ];

        [[1010, 'data1'], [5010, 'data5'], [10010, 'data10'], [25010, 'data25']].forEach((ds: string[] | number[]) => {
            let data: { [key: string]: string | object }[] = this.commonData.slice();
            this.dataSource[ds[1]] = data;
        });
    }

    private template: string = '<div class="e-list-wrapper e-list-avatar">' +
        '<span class="e-avatar e-avatar-circle ${icon} ${$imgUrl ? \'hideUI\' : \'showUI\' }">' +
        '${icon}</span> <img class="e-avatar e-avatar-circle ${$imgUrl ? \'showUI\' : \'hideUI\' }" ' +
        'src="${$imgUrl ?  $imgUrl : \' \' }" />' +
        '<span class="e-list-content">${name}</span></div>';

    private onSelect(e: SelectEventArgs): void {
        let listid: string = (e.item as HTMLElement).dataset.uid;
        switch (listid) {
            case '1':
                this.splitterInstance.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="http://ej2.syncfusion.com/react/demos/src/splitter/images/margaret.png" alt="margaret"></div></div><div class="profile-name">Margeret Peacock</div><table><tr><td class="e-bold">Title</td><td>Sales Representative</td></tr><tr><td class="e-bold">Hire Date</td><td>11/15/1994</td></tr><tr><td class="e-bold">Address</td><td>507 - 20th Ave. E. Apt. 2A</td></tr><tr><td class="e-bold">City</td><td>Seattle</td></tr><tr><td class="e-bold">Phone</td><td>(206) 555-9857</td></tr></table>';
                break;
            case '2':
                this.splitterInstance.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="http://ej2.syncfusion.com/react/demos/src/splitter/images/laura.png" alt="laura"></div><div class="profile-name">Laura Callahan</div><table><tr><td class="e-bold">Title</td><td>Sales Representative</td></tr><tr><td class="e-bold">Hire Date</td><td>09/25/1993</td></tr><tr><td class="e-bold">Address</td><td>908 W. Capital Way</td></tr><tr><td class="e-bold">City</td><td>Tacoma</td></tr><tr><td class="e-bold">Phone</td><td>(206) 555-9482</td></tr></table>';
                break;
            case '3':
                this.splitterInstance.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="http://ej2.syncfusion.com/react/demos/src/splitter/images/robert.png" alt="robert"></div><div class="profile-name">Robert King</div><table><tr><td class="e-bold">Title</td><td>Sales Manager</td></tr><tr><td class="e-bold">Hire Date</td><td>03/20/1990</td></tr><tr><td class="e-bold">Address</td><td>14 Garrett Hill</td></tr><tr><td class="e-bold">City</td><td>London</td></tr><tr><td class="e-bold">Phone</td><td>(71) 555-4848</td></tr></table>';
                break;
            case '4':
                this.splitterInstance.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="http://ej2.syncfusion.com/react/demos/src/splitter/images/michale.png" alt="michale"></div><div class="profile-name">Michale Suyama</div><table><tr><td class="e-bold">Title</td><td>Inside Sales Coordinator</td></tr><tr><td class="e-bold">Hire Date</td><td>06/10/1998</td></tr><tr><td class="e-bold">Address</td><td>4726 - 11th Ave. N.E.</td></tr><tr><td class="e-bold">City</td><td>Seattle</td></tr><tr><td class="e-bold">Phone</td><td>(206) 555-1189</td></tr></table>';
                break;
            case '5':
                this.splitterInstance.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="http://ej2.syncfusion.com/react/demos/src/splitter/images/albert.png" alt="albert"></div><div class="profile-name">Albert Dodsworth</div><table><tr><td class="e-bold">Title</td><td>Sales Representative</td></tr><tr><td class="e-bold">Hire Date</td><td>10/5/1996</td></tr><tr><td class="e-bold">Address</td><td>7 Houndstooth Rd.</td></tr><tr><td class="e-bold">City</td><td>London</td></tr><tr><td class="e-bold">Phone</td><td>(71) 555-4444</td></tr></table>';
                break;
        }
    }
    private onActionComplete(): void {
        this.listViewObj.selectItem(this.dataSource.data1[0]);
    }
    private onCreate(): void {
        this.splitterInstance.element.querySelectorAll('.e-pane')[0].setAttribute('id', 'ui-list');
        this.splitterInstance.element.querySelectorAll('.e-pane')[1].setAttribute('id', 'content');
        this.liElement = this.splitterInstance.element.querySelector("#ui-list");
        if (Browser.isDevice) {
            this.liElement.classList.add('ui-mobile');
        }
    }

    private ListViewContent(): JSX.Element {
        return(
            <ListViewComponent className='splitter-list' height='289' dataSource={this.dataSource.data1}
            enableVirtualization={true} cssClass={'e-list-template'} template={this.template} select={this.onSelect.bind(this)}
            actionComplete={this.onActionComplete.bind(this)}
            ref={(listview) => { this.listViewObj = listview }}>
            <Inject services={[Virtualization]} /> </ListViewComponent>
        );
    };

    public render(): JSX.Element {
        return (
            <div id="target" className="control-section details-view" >
                <SplitterComponent id="detailSplitter" height="292px" width="100%" ref={(splitter) => { this.splitterInstance = splitter }}
                    created={this.onCreate.bind(this)}>
                    <PanesDirective>
                        <PaneDirective size={this.firstPaneSize} min={this.firstPaneMinSize} content={this.ListViewContent.bind(this)}>
                        </PaneDirective>
                        <PaneDirective size={this.secondPaneSize} min={this.secondPaneMinSize}>
                        </PaneDirective>
                    </PanesDirective>
                </SplitterComponent>
            </div>
        );
    }

}
ReactDOM.render(<DetailsView />, document.getElementById('sample'));

